mapper.insert:
insert into `admin` (
    username, password, name, avatar, role,
    admin_no, department, phone, permission_level,
    create_time, update_time, status
) values (
             #{username}, #{password}, #{name}, #{avatar}, #{role},
             #{adminNo}, #{department}, #{phone}, #{permissionLevel},
             #{createTime}, #{updateTime}, #{status}
         )

mapper.update:
update `admin`
username = #{username}, password = #{password}, name = #{name}, avatar = #{avatar}, admin_no = #{adminNo}, department = #{department}, phone = #{phone}, permission_level = #{permissionLevel}, status = #{status}, last_login_time = #{lastLoginTime}, login_count = #{loginCount}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select count(*) from `admin`


        and status = #{status}


        and department = #{department}

mapper.update:
update `admin`
set last_login_time = #{lastLoginTime},
    login_count = login_count + 1,
    update_time = #{updateTime}
where id = #{id}

mapper.select:
select * from `admin`

    and username = #{username}
    and password = #{password}
    and status = #{status}
    and role = #{role}

limit 1
