mapper.select:
select * from `student`


        and name like concat('%', #{name}, '%')


        and student_no like concat('%', #{studentNo}, '%')


        and grade = #{grade}


        and major like concat('%', #{major}, '%')


        and class_id = #{classId}


        and academic_status = #{academicStatus}


        and status = #{status}


        and enrollment_year = #{enrollmentYear}


order by student_no asc

select * from `student`

    and username = #{username}
    and password = #{password}
    and status = #{status}

limit 1

mapper.update:
update `student`
username = #{username}, password = #{password}, name = #{name}, avatar = #{avatar}, student_no = #{studentNo}, class_id = #{classId}, class_name = #{className}, grade = #{grade}, major = #{major}, enrollment_year = #{enrollmentYear}, phone = #{phone}, email = #{email}, academic_status = #{academicStatus}, warning_count = #{warningCount}, status = #{status}, update_time = #{updateTime}
where id = #{id}

mapper.select:
select count(*) from `student`


        and grade = #{grade}


        and major = #{major}


        and academic_status = #{academicStatus}


        and status = #{status}

select * from `student`


        and name like concat('%', #{student.name}, '%')


        and student_no like concat('%', #{student.studentNo}, '%')


        and grade = #{student.grade}


        and major like concat('%', #{student.major}, '%')


        and class_id = #{student.classId}


        and academic_status = #{student.academicStatus}


order by student_no asc
limit #{offset}, #{pageSize}
